前言

归档页

```html
 <!--<%- list_posts({order: -1, amount: 1000}) %>-->

<!-- 完全显示 -->

<% var last; %>
<% site.posts.sort('-date').map(function(post, i){ %>
    <% var year = post.date.year(); %>
    <% if (last != year){ %>

        <% if (last != null){ %>
            </div></section>
        <% } %>

        <% last = year; %>
        <section>
        <div>
            <a href="<%- url_for(config.archive_dir + '/' + year) %>" class="archive-year">
                <%= year %>
            </a>
        </div>
        <div class="archives">
    <% } %>
    <%- partial('_partial/archive-post', {post: post, even: i % 2 == 0, index: true}) %>
<% }) %>

<% if (site.posts.length){ %>
        </div></section>
<% } %>
```

